import React, { useRef } from "react";
import { Card, Button, Tooltip, Image } from "antd";
import "./Card.scss";

interface ServiceCardProps {
  title: string;
  imageUrl: string;
  price?: number;
  specialPrice?: number;
  onDetailClick?: () => void;
  onOrderClick?: () => void;
  onConsultClick?: (service: any) => void;
}

const CustomCard: React.FC<ServiceCardProps> = ({
  title,
  imageUrl,
  onDetailClick,
  onConsultClick,
  onOrderClick,
  price,
  specialPrice,
}) => {
  return (
    <Tooltip title={title} placement="rightTop">
      <Card
        className="card"
        cover={
          <div className="ant-card-cover">
            {imageUrl ? (
              <Image
                alt={title}
                src={imageUrl}
                fallback="data:image/png;base64,"
                preview={false}
                className="card-image"
              />
            ) : (
              <img src={imageUrl} alt={title} />
            )}
          </div>
        }
        hoverable
      >
        <div className="card-content">
          <h3>{title}</h3>
          {price && (
            <p>
              Giá:{" "}
              <i>
                <del>
                  {(price ?? 0).toLocaleString("vi-VN", {
                    style: "currency",
                    currency: "VND",
                  } as Intl.NumberFormatOptions)}
                </del>
              </i>
              &nbsp;&nbsp;
              <b style={{ color: "red", fontSize: "16px" }}>
                {(specialPrice ?? 0).toLocaleString("vi-VN", {
                  style: "currency",
                  currency: "VND",
                } as Intl.NumberFormatOptions)}
              </b>
            </p>
          )}

          <div className="card-actions">
            <Button
              type="default"
              onClick={() => onConsultClick({ name: title })}
            >
              Nhận tư vấn
            </Button>
            {onOrderClick ? (
              <Button onClick={onOrderClick} className="btn-detail">
                Đặt hàng
              </Button>
            ) : (
              <Button onClick={onDetailClick} className="btn-detail">
                Xem chi tiết
              </Button>
            )}
          </div>
        </div>
      </Card>
    </Tooltip>
  );
};

export default CustomCard;
